import json

import requests

class TestCountryCodes():
    def test_country_codes(self):
        url_countryCode = 'https://api.mobi.alif.cloud/api/mobi/v1/location/countryCodes'
        body = {}
        headers = {
            "AppVersion": "Android SDK:29(10),v.3.0.3.495"
                  }

        response = requests.get(url=url_countryCode, json=body, headers=headers)
        response_as_dict = response.json()
        assert response_as_dict ["code"] == 200
        assert response_as_dict ["message"] == "Операция успешно выполнена"
